import { useEffect, useState } from "react";
import api from "../services/api";
import "../styles/global.css";

export default function Clientes() {
  const [clientes, setClientes] = useState([]);

  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    observacoes: "",
    anamnese: {
      alergias: "",
      doencas: "",
      medicamentos: "",
      procedimentos_anteriores: "",
      observacoes: "",
    },
  });

  useEffect(() => {
    carregarClientes();
  }, []);

  function carregarClientes() {
    api.get("/clientes").then((res) => setClientes(res.data));
  }

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleAnamneseChange(e) {
    setForm({
      ...form,
      anamnese: { ...form.anamnese, [e.target.name]: e.target.value },
    });
  }

  function handleSubmit(e) {
    e.preventDefault();

    api.post("/clientes", form).then(() => {
      setForm({
        nome: "",
        telefone: "",
        email: "",
        observacoes: "",
        anamnese: {
          alergias: "",
          doencas: "",
          medicamentos: "",
          procedimentos_anteriores: "",
          observacoes: "",
        },
      });
      carregarClientes();
    });
  }

  return (
    <div className="page">
      <h1>Clientes</h1>

      <form className="card" onSubmit={handleSubmit}>
        <h3>Dados do Cliente</h3>

        <input
          name="nome"
          placeholder="Nome"
          value={form.nome}
          onChange={handleChange}
          required
        />

        <input
          name="telefone"
          placeholder="Telefone"
          value={form.telefone}
          onChange={handleChange}
        />

        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
        />

        <textarea
          name="observacoes"
          placeholder="Observações"
          value={form.observacoes}
          onChange={handleChange}
        />

        <h3>Anamnese</h3>

        <textarea
          name="alergias"
          placeholder="Alergias"
          value={form.anamnese.alergias}
          onChange={handleAnamneseChange}
        />

        <textarea
          name="doencas"
          placeholder="Doenças"
          value={form.anamnese.doencas}
          onChange={handleAnamneseChange}
        />

        <textarea
          name="medicamentos"
          placeholder="Medicamentos"
          value={form.anamnese.medicamentos}
          onChange={handleAnamneseChange}
        />

        <textarea
          name="procedimentos_anteriores"
          placeholder="Procedimentos anteriores"
          value={form.anamnese.procedimentos_anteriores}
          onChange={handleAnamneseChange}
        />

        <textarea
          name="observacoes"
          placeholder="Observações da anamnese"
          value={form.anamnese.observacoes}
          onChange={handleAnamneseChange}
        />

        <button type="submit">Cadastrar Cliente</button>
      </form>

      <div className="card">
        <h3>Clientes Cadastrados</h3>
        <ul>
          {clientes.map((c) => (
            <li key={c.id}>
              <strong>{c.nome}</strong> — {c.telefone}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
